cubegui_config_data*
cubegui_config_get_frontend_data( void )
{
    static cubegui_config_data data;

    data.m_qmake                 = "/opt/eb/software/Compiler/GCCcore/6.4.0/Qt/4.8.7/bin/qmake";
    data.m_moc                   = "/opt/eb/software/Compiler/GCCcore/6.4.0/Qt/4.8.7/bin/moc";
    data.m_uic                   = "/opt/eb/software/Compiler/GCCcore/6.4.0/Qt/4.8.7/bin/uic";
    data.m_rcc                   = "/opt/eb/software/Compiler/GCCcore/6.4.0/Qt/4.8.7/bin/rcc";
    data.m_cubelib_config        = "intern";
    data.m_cc                    = " gcc";
    data.m_cxx                   = " g++";
    data.m_cppflags              = "-I/home/builder/builder/atlantis/builds/ByzjsFMR/0/perftools/addons/cube/supplements/bundle/CubeBundle-/vpath/_install/include/cubegui";
    data.m_incldir               = "/home/builder/builder/atlantis/builds/ByzjsFMR/0/perftools/addons/cube/supplements/bundle/CubeBundle-/vpath/_install/include/cubegui";
    data.m_use_rpath_flag        = 1;
    data.m_rpath_flag_cc         = "$wl-rpath $wl$libdir";
    data.m_wl_flag               = "-Wl,";
    data.m_aix_libpath           = "";
    data.m_sys_lib_dlsearch_path = "/lib /usr/lib /usr/local/lib /lib/x86_64-linux-gnu /usr/lib/x86_64-linux-gnu ";

    deque < string > libs;
    deque < string > ldflags;
    deque < string > rpaths;
    deque < string > dependency_las;

    /* cubegui_config_library_dependencies_frontend_inc.hpp is
       generated by build-config/common/generate-library-dependency.sh */
    add_library_dependencies_frontend( libs, ldflags, rpaths, dependency_las, &data.m_la_objects );

    data.prepare_rpath_flags();

    return &data;
}
